            sender, null,
            row.TaxPeriodType != PX.Objects.TX.VendorTaxPeriodType.FiscalPeriod);
        PXUIFieldAttribute.SetEnabled<Vendor.taxReportPrecision>(
            sender, null, row.TaxUseVendorCurPrecision != true);
