protected void Batch_ManualStatus_FieldUpdating(PXCache sender, PXFieldUpdatingEventArgs e)
{
    Batch batch = (Batch)e.Row;
    if (batch != null && e.NewValue != null)
    {
        switch ((string)e.NewValue)
        {
            case "H":
                batch.Hold = true;
                batch.Released = false;
                batch.Posted = false;
                break;
            case "B":
                batch.Hold = false;
                batch.Released = false;
                batch.Posted = false;
                break;
            case "U":
                batch.Hold = false;
                batch.Released = true;
                batch.Posted = false;
                break;
            case "P":
                batch.Hold = false;
                batch.Released = true;
                batch.Posted = true;
                break;
        }
   }
}
 
protected void Batch_ManualStatus_FieldSelecting(PXCache sender, PXFieldSelectingEventArgs e)
{
    Batch batch = (Batch)e.Row;
    if (batch != null)
    {
        if (batch.Hold == true)
        {
            e.ReturnValue = "H";
        }
        else if (batch.Released != true)
        {
            e.ReturnValue = "B";
        }
        else if (batch.Posted != true)
        {
            e.ReturnValue = "U";
        }
        else
        {
            e.ReturnValue = "P";
        }
    }
}

protected void _(Events.FieldUpdating<Batch.ManualStatus> e)
{
    Batch batch = (Batch)e.Row;
    if (batch != null && e.NewValue != null)
    {
        switch ((string)e.NewValue)
        {
            case "H":
                batch.Hold = true;
                batch.Released = false;
                batch.Posted = false;
                break;
            case "B":
                batch.Hold = false;
                batch.Released = false;
                batch.Posted = false;
                break;
            case "U":
                batch.Hold = false;
                batch.Released = true;
                batch.Posted = false;
                break;
            case "P":
                batch.Hold = false;
                batch.Released = true;
                batch.Posted = true;
                break;
        }
   }
}
 
protected void _(Events.FieldSelecting<Batch.ManualStatus> e)
{
    Batch batch = (Batch)e.Row;
    if (batch != null)
    {
        if (batch.Hold == true)
        {
            e.ReturnValue = "H";
        }
        else if (batch.Released != true)
        {
            e.ReturnValue = "B";
        }
        else if (batch.Posted != true)
        {
            e.ReturnValue = "U";
        }
        else
        {
            e.ReturnValue = "P";
        }
    }
}

