    .WithActions(actions =>
    {
        actions.Add(actionAssign, a => a
            .IsDuplicatedInToolbar());
 
        actions.Add(actionAutoAssign, a => a
            .IsAutoAction(ownerNotNullCondition));
        actions.Remove("Open");
    }
));

