    .WithFieldStates(fields =>
    {
        fields.UpdateField<CROpportunity.resolution>(field =>
            field.WithDefaultValue(_reasonUnassign).ComboBoxValues(_reasonUnassign, _reasonCanceled, _reasonOther, _reasonRejected));
    })
);

